import React, { Component, createContext } from 'react';
import apiClient from '../helpers/apiClient';
import socketIOClient from "socket.io-client";

const AppContext = createContext();
const { Provider } = AppContext;

export default class AppContextProvider extends Component {
  state = {
    //서울시청 초기화
    lat: 37.5666035,
    lng: 126.9783868,

    // 현재 Login 상태에 대한 state 
    // status : false -> 로그인 x 
    // status : true -> 로그인 o
    signInInfo: {
      status : false,
      id: '',
      email : ''
    },

    // 소켓 Obj state 
    // io : null -> 소켓 연결 x 
    socketConnection : {
      io : null
    }
  }

  actions = {
    addContents: formData => apiClient.post('/contents', formData),
    getContentsR1: () => apiClient.get('/contents/r1'),
    getContentsR2: () => apiClient.get('/contents/r2'),
    getContentsR3: () => apiClient.get('/contents/r3'),
    getContentsR4: () => apiClient.get('/contents/r4'),
    getContentsR5: () => apiClient.get('/contents/r5'),
    getContentsByCategory: searchTerm => apiClient.get(`/contents/context/${searchTerm}`), //메인 검색창에서 카테고리 검색 시 데이터 보여줌
    getContentsDetail: detailTerm => apiClient.get(`/contents/detail/${detailTerm}`), //상세내용 보여줌
    getCurrentPosition: () => {
      navigator.geolocation.getCurrentPosition((position) => {
        return this.setState({
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        });
      });
    },

    // 개발용/ 이후 간략하게 수정
    checkAuth : async () => {
      return apiClient.post('/users/checkAuth')
        .then(res => ({status: res.status, id: res.id, email: res.email}))
        .then(user => {
          console.log(user);
          let io = this.state.socketConnection.io;
          const signInStatus = user.status;

          if (io || !signInStatus){
            this.setState({
              ...this.state,
              signInInfo: {
                status: user.status,
                id : user.id,
                email: user.email}
            })
          }
          else{
            io =  socketIOClient('http://localhost:8080');

            this.setState({
              ...this.state,
              socketConnection:{io: io},
              signInInfo: {
                status: user.status,
                id : user.id,
                email: user.email}
            })
          }
        })
        .catch(err=> console.log(err)); 
  }

    
  }

  render() {
    const { children } = this.props;
    return(
      <Provider value={{ state: this.state, actions: this.actions }}>{ children }</Provider>
    );
  }
}

export {
  AppContext,
};
